mapper.select:
select drug_id, sum(quantity) as quantity from mbgl.t_stock
where drug_id in

    #{item}

and quantity > 0
GROUP BY drug_id order by FIELD(drug_id,

    #{item}

select id,drug_id,quantity,version from mbgl.t_stock where drug_id in
#{item}
and quantity > 0 order by FIELD(drug_id,
#{item}
,quantity

mapper.update:
update mbgl.t_stock
set quantity = quantity - #{quantity},
    version  = version + 1
where id = #{id}
  and quantity > #{quantity}
